import React from 'react';
import { Link } from 'react-router-dom';
import Gravatar from '../components/Gravatar';
import './styles/BadgesList.css';

class BadgesListItem extends React.Component {
  render() {
    return (
      <div className="BadgesListItem">
        <Gravatar
          className="BadgesListItem__avatar"
          email={this.props.badge.email}
          alt={`${this.props.badge.firstName} ${this.props.badge.lastName}`}
         />
        <div>
          <strong>
            {this.props.badge.firstName} {this.props.badge.lastName}
          </strong>
          <br />@{this.props.badge.twitter}
          <br />
          {this.props.badge.jobTitle}
        </div>
      </div>
    );
  }
}

function BadgesList(props) {
  const badges = props.badges;
  const [ query, setQuery ] = React.useState('');
  // para poder filtrar:
  const filteredBadgers = badges.filter(badge => {
    return badge.firstName.includes(query)
  })
    if (filteredBadgers.length === 0) {
      return (
        <div>
          <h3>No badges were found</h3>
          <Link className="btn btn-primary" to="/badges/new">
            Create new badge
          </Link>
        </div>
      );
    }
    return (
      <div className="BadgesList">
        <div className="form-group">
          <label>please,filter badger platzi lover </label>
          <input
          className="form-control"
          // lo que sea que este en el estado del query, 
          //se va a desplegar en el value
          value={query}
          onChange={e => {
            setQuery(e.target.value)
          }}
          />
        </div>
        <ul className="list-unstyled">
          {filteredBadgers.map(badge => {
            return (
              <li key={badge.id}>
                <Link 
                  to={`/badges/${badge.id}/edit`}
                  className="text-reset text-decoration-none"
                  >
                  <BadgesListItem badge={badge} />
                </Link>
              </li>
            );
          })}
        </ul>
      </div>
    );
}

export default BadgesList;
